<?phpclass Html2Helper extends AppHelper {	var $helpers = array('Form','Html');	function image($record,$field,$path,$imageFlag=true){		echo $this->Form->input($field,array('type'=>'file','label'=>false, 'div'=>false));		if(!empty($record[$this->model()][$field])){			echo "&nbsp;&nbsp;&nbsp";			if($imageFlag)							echo $this->Html->image('magnifire.jpeg',array('class'=>'magnifier','path'=>$this->base.'/'.$path."/".$record[$this->model()][$field]));			else				echo $this->Html->image('magnifire.jpeg',array('class'=>'magnifier', 'imageFlag'=>"false", 'path'=>$this->base.'/'.$path."/".$record[$this->model()][$field]));			echo $this->Form->input($this->model().".".$field.'.remove',array('type'=>'hidden', 'value'=>''));			echo "&nbsp;&nbsp;&nbsp;";			echo $this->Html->image('delete.png',array('class'=>'delete'));		}	}	function lightbox(){		echo "<div id=\"lightbox-panel\">";		echo "<p class=\"closeSection\">";		echo $this->Html->image('close.png', array('class'=>'close')); 		echo "</p>";		echo $this->Html->image('noimage.jpg',array('class'=>'preview'));		echo "</div><div id=\"lightbox\"> </div>";	}}?>