<?phpclass Html4Helper extends AppHelper {	var $helpers = array('Form','Html');	function image($record,$field,$fullFieldUrl,$imageFlag=true){		echo $this->Form->input($field,array('type'=>'file','label'=>false, 'div'=>false));		if(!empty($record[$this->model()][$fullFieldUrl])){			echo "&nbsp;&nbsp;&nbsp";			if($imageFlag)                            echo '<a class="fancybox" rel="group" href="'.$record[$this->model()][$fullFieldUrl].'">'.$this->Html->image('magnifire.jpeg',array('class'=>'magnifiers')).'</a>';                        			else                            echo $this->Html->image('magnifire.jpeg',array('class'=>'magnifier', 'imageFlag'=>"false", 'path'=>$this->base.'/'.$path."/".$record[$this->model()][$field]));                        			echo $this->Form->input($this->model().".".$field.'.remove',array('type'=>'hidden', 'value'=>''));			echo "&nbsp;&nbsp;&nbsp;";			echo $this->Html->image('delete.png',array('class'=>'delete'));		}	}}?>